TABLEATTRIBUTE.VALUE:
SELECT SESSION_NAME||CHR(10)||INCR_START_VALUE||CHR(10)||INCR_END_VALUE||CHR(10) FROM ( SELECT
'['||FOLDER||'.WF:'||WORKFLOW||'.ST:'||SESSION_NAME ||']' AS SESSION_NAME, CASE INCR_TYPE WHEN 'ID INT' THEN '$$INCR_START_ID='||INCR_START_VALUE WHEN 'TIME' THEN '$$INCR_START_DT='||INCR_START_VALUE END AS INCR_START_VALUE, CASE INCR_TYPE WHEN 'ID INT' THEN CASE NVL(INCR_END_VALUE,'0') WHEN '0' THEN '' ELSE '$$INCR_END_DT='||INCR_END_VALUE END WHEN 'TIME' THEN '$$INCR_END_DT='||INCR_END_VALUE END AS INCR_END_VALUE FROM ETLMGR.ETL_INCR_PARAM P WHERE P.INCR_TYPE <> 'FULL' AND FOLDER = '$PMFolderName' AND WORKFLOW = '$PMWorkflowName' AND SESSION_NAME = '$$SESSION_NAME' AND TARGET_OWNER = '$$TARGET_OWNER' AND TARGET_TABLE = '$$TARGET_TABLE'
) PARAM
